// Access the list of results from the input
const results = $input.first().json.results;

// Filter and extract LinkedIn URLs
const linkedinUrls = results
  .filter(result => result.url.includes("linkedin.com")) // Check if the URL contains "linkedin.com"
  .map(result => ({ json: { linkedinUrl: result.url } })); // Map to clean output

// Return the filtered LinkedIn URLs
return linkedinUrls;

